import { Component, OnInit } from '@angular/core';
import {SharedService} from '../shared.service';
import {FormGroup, FormControl,Validators} from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';

import {COMMA, ENTER} from '@angular/cdk/keycodes';
import {ElementRef, ViewChild} from '@angular/core';
import {MatAutocompleteSelectedEvent, MatAutocomplete} from '@angular/material/autocomplete';
import {MatChipInputEvent} from '@angular/material/chips';
import {Observable} from 'rxjs';
import {map, startWith} from 'rxjs/operators';
import {Opening} from '../shared/opening.model';
import { NotificationService } from '../shared/notification.service';

@Component({
  selector: 'app-comp2',
  templateUrl: './comp2.component.html',
  styleUrls: ['./comp2.component.css'],
  providers: [SharedService]
})

export class Comp2Component implements OnInit {

  validPattern = /^[a-zA-Z0-9_]*$/;

  openingForm = new FormGroup(
  { 
    title: new FormControl('', [Validators.required,Validators.pattern(this.validPattern)]),
    loc : new FormControl(0, [Validators.required]),
    Etype: new FormControl(0,[Validators.required]),
    eligibility: new FormControl('', [Validators.required,Validators.pattern(this.validPattern)]),
    work: new FormControl('',[Validators.pattern(this.validPattern)]),
    note: new FormControl('',[Validators.pattern(this.validPattern)]),
    skills: new FormControl([])
  });


  visible = true;
  selectable = true;
  removable = true;
  separatorKeysCodes: number[] = [ENTER, COMMA];
  fruitCtrl = new FormControl();
  filteredFruits: Observable<string[]>;
  selectedSkills: string[] = [];
  allFruits: string[] = ['JavaScript', 'NodeJs', 'AngularJs', 'MEAN Stack', 'Data Structures'];

  @ViewChild('fruitInput') fruitInput: ElementRef<HTMLInputElement>;
  @ViewChild('auto') matAutocomplete: MatAutocomplete;

  constructor(public service: SharedService,
    public notificationService: NotificationService,
   public dialogRef: MatDialogRef<Comp2Component>) { 

      this.filteredFruits = this.fruitCtrl.valueChanges.pipe(
          startWith(null),
          map((fruit: string | null) => fruit ? this._filter(fruit) : this.allFruits.slice()));
  }



  Locations=[
        {id: 1, value: 'Mumbai'},
        {id: 2, value: 'Pune'},
        {id: 3, value: 'Bangalore'}
  ];

  EmpTypes=[
    {id: 1, value: 'Full Time'},
    {id: 2, value: 'Part Time'},
    {id: 3, value: 'Trainee'}
  ];


  initializeFormGroup() {
    this.openingForm.setValue({
      title: '',
      loc : 0,
      EType: 0,
      eligibility: '',
      work: '',
      note: '',
      skills: ['']
    });
  }


  onClose() {
    this.openingForm.reset();
    this.service.initializeFormGroup();
    this.dialogRef.close();
  }

  onReset() {
    this.openingForm.reset();
    this.initializeFormGroup();
  }


  ngOnInit(): void {
    this.service.getOpenings()
       .subscribe((openings:any) => 
            this.openings = openings)
  }
    add(event: MatChipInputEvent): void {
      const value = (event.value || '').trim();

      // Add our fruit
      if (value) {
        this.selectedSkills.push(value);
      }

      // Clear the input value
      event.chipInput!.clear();

      this.fruitCtrl.setValue(null);
    }

    remove(fruit: string): void {
      const index = this.selectedSkills.indexOf(fruit);

      if (index >= 0) {
        this.selectedSkills.splice(index, 1);
      }
    }

    selected(event: MatAutocompleteSelectedEvent): void {
      this.selectedSkills.push(event.option.viewValue);
      this.fruitInput.nativeElement.value = '';
      this.fruitCtrl.setValue(null);
    }

    private _filter(value: string): string[] {
      const filterValue = value.toLowerCase();

      return this.allFruits.filter(fruit => fruit.toLowerCase().indexOf(filterValue) === 0);
    }

    openings: Opening[] = [];
    opening : Opening;

    jobtitle        : string;
    location        : string;
    EmployementType : string;
    Eligibility     : string;
    Work            : string;
    Note            : string;
    skills          : Array<string>;


    addOpening(){

      if(this.openingForm.valid){  
            const newOpening = {
              jobtitle        : this.jobtitle,
              location        : this.Locations[parseInt(this.location)-1].value,
              EmployementType : this.EmpTypes[parseInt(this.EmployementType)-1].value,
              Eligibility     : this.Eligibility,
              Work            : this.Work,
              Note            : this.Note,
              skills          : this.selectedSkills
          } 

          //console.log(this.selectedSkills);
            this.service.addOpening(newOpening)
                .subscribe((opening:any) => {
                    this.openings.push(opening);
                });
            //console.log(this.openingForm.value);
            this.openingForm.reset();
            this.service.initializeFormGroup();
            this.notificationService.success(':: Submitted successfully');
            this.onClose();
      }

    }

}


<div class="container">
  <mat-toolbar class="tool1">
    <span>Add new opening</span>
    <span class="fill-remaining-space"></span>
    <button
      class="btn-dialog-close"
      mat-stroked-button
      (click)="onClose()"
      tabIndex="-1"
    >
      <mat-icon>clear</mat-icon>
    </button>
  </mat-toolbar>
</div>

<br />

<form [formGroup]="openingForm" (submit)="addOpening()">
  <input type="hidden" name="_id" />
  <mat-grid-list cols="1" rowHeight="100px" [gutterSize]="'5px'">
    <mat-grid-tile>
      <div class="controles-container">
        <!--<input type="hidden" formControlName="$key" />-->
        <mat-form-field class="example-full-width">
          <mat-label style="color: black; font-size: large"
            ><b>Job Title:</b></mat-label
          >
          <input
            matInput
            #message
            maxlength="50"
            [(ngModel)]="jobtitle"
            placeholder="Job Title"
            formControlName="title"
            required
          />
          <mat-error *ngIf="openingForm.controls['title'].errors?.required"
            >Job title is Mandatory</mat-error
          >
          <mat-error *ngIf="openingForm.controls['title'].errors?.pattern"
            >Only Alpha Numericals are allowed</mat-error
          >
          <mat-hint align="start"><strong>Job Title</strong> </mat-hint>
          <mat-hint align="end">{{ message.value.length }} / 50</mat-hint>
        </mat-form-field>
        &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;
        <mat-form-field>
          <mat-label style="color: black; font-size: large"
            ><b>Location:</b></mat-label
          >
          <mat-select
            [(ngModel)]="location"
            placeholder="Location"
            formControlName="loc"
            required
          >
            <mat-option>None</mat-option>
            <ng-container *ngFor="let Location of Locations">
              <mat-option value="{{ Location.id }}">{{
                Location.value
              }}</mat-option>
            </ng-container>
          </mat-select>
          <mat-error>Location is Mandatory</mat-error>
          <mat-hint align="start"><strong>Location</strong> </mat-hint>
        </mat-form-field>
        &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;
        &nbsp; &nbsp;
        <mat-form-field>
          <mat-label style="color: black; font-size: large"
            ><b>Employement type:</b></mat-label
          >
          <mat-select
            [(ngModel)]="EmployementType"
            placeholder="Employement type"
            formControlName="Etype"
            required
          >
            <mat-option>None</mat-option>
            <ng-container *ngFor="let type of EmpTypes">
              <mat-option value="{{ type.id }}">{{ type.value }}</mat-option>
            </ng-container>
          </mat-select>
          <mat-error>Employement Type is Mandatory</mat-error>
        </mat-form-field>
      </div>
    </mat-grid-tile>
    <mat-grid-tile>
      <div class="controles-container">
        <mat-form-field>
          <mat-label style="color: black; font-size: large"
            ><b>Eligibility:</b></mat-label
          >
          <input
            matInput
            [(ngModel)]="Eligibility"
            placeholder="Eligibility"
            formControlName="eligibility"
            required
          />
          <mat-hint align="start"><strong>Eligibility</strong> </mat-hint>
        
          <mat-error
            *ngIf="openingForm.controls['eligibility'].errors?.required"
            >This field is Mandatory</mat-error
          >
          <mat-error *ngIf="openingForm.controls['eligibility'].errors?.pattern"
            >Only Alpha Numericals</mat-error
          >
        </mat-form-field>
        &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;
        <mat-form-field>
          <mat-label style="color: black; font-size: large"
            ><b>Work will include:</b></mat-label
          >
          <input matInput [(ngModel)]="Work" formControlName="work" />
          <mat-hint align="start"><strong>Work will include</strong> </mat-hint>
          <mat-error *ngIf="openingForm.controls['work'].errors?.pattern"
            >Only Alpha Numericals</mat-error
          >
        </mat-form-field>
        &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;
        &nbsp; &nbsp;
        <mat-form-field>
          <mat-label style="color: black; font-size: large"
            ><b>Note:</b></mat-label
          >
          <input
            matInput
            [(ngModel)]="Note"
            placeholder="Note"
            formControlName="note"
          />
          <mat-hint align="start"><strong>Note</strong> </mat-hint>
        </mat-form-field>
      </div>
    </mat-grid-tile>

    <mat-grid-tile>
      <mat-form-field class="skills">
        <mat-label style="color: black; font-size: large"
          ><b>Skills:</b></mat-label
        >
        <mat-chip-list #chipList aria-label="Fruit selection">
          <mat-chip
            *ngFor="let fruit of selectedSkills"
            [selectable]="selectable"
            [removable]="removable"
            (removed)="remove(fruit)"
          >
            {{ fruit }}
            <mat-icon matChipRemove *ngIf="removable">cancel</mat-icon>
          </mat-chip>
          <input
            #fruitInput
            [(ngModel)]="skills"
            formControlName="skills"
            [matAutocomplete]="auto"
            [matChipInputFor]="chipList"
            [matChipInputSeparatorKeyCodes]="separatorKeysCodes"
            (matChipInputTokenEnd)="add($event)"
          />
        </mat-chip-list>
        <mat-autocomplete
          #auto="matAutocomplete"
          (optionSelected)="selected($event)"
        >
          <mat-option
            *ngFor="let fruit of filteredFruits | async"
            [value]="fruit"
          >
            {{ fruit }}
          </mat-option>
        </mat-autocomplete>
        <mat-hint align="start"><strong>Skills</strong> </mat-hint>
      </mat-form-field>
    </mat-grid-tile>

    <mat-grid-tile>
      <div class="button-row">
        <button
          class="btn1"
          mat-raised-button
          color="primary"
          type="submit"
          [disabled]="openingForm.invalid"
        >
          Save
        </button>
        &nbsp; &nbsp; &nbsp;&nbsp;
        <button class="btn2" mat-raised-button (click)="onReset()">
          Reset
        </button>
      </div>
    </mat-grid-tile>
  </mat-grid-list>
</form>
